import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:shaded/core/functions/transitions.dart';class OnBoardingController extends GetxController {  PageController pageController = PageController(initialPage: 0);  int currentScreen = 0;  int lengthPages = 3;  next(context) {    currentScreen++;    if (currentScreen >= lengthPages) {      push_replcement(        context,         Container(),      );    } else {      update();    }  }  skip(context) {    currentScreen = 3;    next(context);  }}