// ignore_for_file: non_constant_identifier_namesimport 'dart:convert';import 'package:dartz/dartz.dart';import 'package:shaded/core/api_services/check_internet.dart';import 'package:shaded/core/api_services/status_request.dart';import 'package:http/http.dart' as http;class ApiServices {  Future<Either<StatusRequest, dynamic>> getData(    String _url, {    Map<String, String>? headers,    Map<String, dynamic>? queryParameters,  }) async {    if (await CheckInternet.checkInternetConnection()) {      try {        Uri url = Uri.https(_url, '', queryParameters);        // Uri url = Uri.parse(_url); في حال صارت مشكلة مع الكود الاول        var response = await http.get(url, headers: headers);        if (response.statusCode == 200) {          Map response_body = jsonDecode(response.body);          return right(response_body);        } else {          print("Error In Api Services getMethod : serverFauilre");          return left(StatusRequest.serverFailure);        }      } catch (e) {        print("Error In Api Services getMethod : failure");        return left(StatusRequest.failure);      }    } else {      print("Error In Api Services getMethod : offlineFailure");      return left(StatusRequest.offlineFailure);    }  }  Future<Either<StatusRequest, Map<dynamic, dynamic>>> postData(    String _url, {    Map<String, dynamic>? body,    Map<String, String>? headers,  }) async {    if (await CheckInternet.checkInternetConnection()) {      try {        Uri url = Uri.parse(_url);        var response = await http.post(url, headers: headers, body: body);        Map response_body = jsonDecode(response.body);        if (response.statusCode == 201) {          return right(response_body);        } else {          print(              "Error In Api Services postMethod : serverFauilre \n The responseBody :*** ${response.body} ***");          // return right(response_body);          return left(StatusRequest.serverFailure);        }      } catch (e) {        print("Error In Api Services postMethod : failure : $e");        return left(StatusRequest.failure);      }    } else {      print("Error In Api Services postMethod : offlineFailure");      return left(StatusRequest.offlineFailure);    }  }}