import 'package:shaded/core/api_services/api_services.dart';import 'package:shaded/core/api_services/status_request.dart';import 'package:shaded/core/app_services/app_link_api.dart';class LoginData {  ApiServices apiServices = ApiServices();  Future login({    required String mobile,    required String fcmToken,    required String deviceType,    required String userType,  }) async {    Map<String, dynamic> body = {      'mobile': mobile,      'fcmToken': fcmToken,      'deviceType': deviceType,      'userType': userType,    };    Map<String, String> headers = {};    try {      return await apiServices          .postData(        AppLinkApi.LOGIN,        body: body,        headers: headers,      )          .then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      print('Error In LoginData : $e');      return StatusRequest.failure;    }  }}void main() async {  await LoginData()      .login(    mobile: '966588346579',    fcmToken: '',    deviceType: 'android',    userType: '1',  )      .then((value) {    print('%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%');    print(value);  });}