import 'package:shaded/core/api_services/api_services.dart';import 'package:shaded/core/api_services/status_request.dart';import '../../../app_services/app_link_api.dart';class LoginData {  ApiServices apiServices = ApiServices();  Future verifyCode({    required String mobile,    required String code,  }) async {    Map<String, dynamic> body = {      'mobile': mobile,      'code': code,    };    Map<String, String> headers = {};    try {      return await apiServices          .postData(        AppLinkApi.VERIFYCODE,        body: body,        headers: headers,      )          .then((value) {        return value;        // return value.fold((l) => l, (r) => r);      });    } catch (e) {      print('Error In LoginData : $e');      return StatusRequest.failure;    }  }}