import 'package:flutter/material.dart';import 'package:shaded/core/size_config/size_config.dart';class CustomText extends StatelessWidget {  String text;  double size;  FontWeight fontWeight;  Color color;  CustomText(    this.text,    this.size,    this.color, {    this.fontWeight = FontWeight.w500,  });  @override  Widget build(BuildContext context) {    return Text(      text,      style: TextStyle(        fontSize: SizeConfig.scaleTextFont(size),        fontWeight: fontWeight,        color: color,        fontFamily: 'Tajawal',      ),    );  }}