// ignore_for_file: camel_case_typesimport 'package:flutter/material.dart';push(context, Widget widget) {  return Navigator.push(    context,    SlideTransitionPageRoute_LeftToRight(page:widget),  );}push_replcement(context, Widget widget) {  return Navigator.pushReplacement(    context,    SlideTransitionPageRoute_LeftToRight(page:widget),  );}pushAndRemoveAll(context, Widget widget) {  return Navigator.pushAndRemoveUntil(    context,    SlideTransitionPageRoute_LeftToRight(page:widget),        (route) => false,  );}pop(context, Widget widget) {  return Navigator.pop(    context,    SlideTransitionPageRoute_LeftToRight(page: widget),  );}Route getRotue(Widget page){  return SlideTransitionPageRoute_LeftToRight(page: page);}class SlideTransitionPageRoute_LeftToRight<T> extends PageRouteBuilder<T> {  final Widget page;  SlideTransitionPageRoute_LeftToRight({required this.page})      : super(      pageBuilder: (          BuildContext context,          Animation<double> animation,          Animation<double> secondaryAnimation,          ) =>      page,      transitionsBuilder: (          BuildContext context,          Animation<double> animation,          Animation<double> secondaryAnimation,          Widget child,          ) =>          SlideTransition(            position: Tween<Offset>(              begin: const Offset(-1.0, 0.0),              end: Offset.zero,            ).animate(animation),            child: child,          ),      transitionDuration: const Duration(milliseconds: 400)  );}class SlideTransitionPageRoute_RightToLeft<T> extends PageRouteBuilder<T> {  final Widget page;  SlideTransitionPageRoute_RightToLeft({required this.page})      : super(      pageBuilder: (          BuildContext context,          Animation<double> animation,          Animation<double> secondaryAnimation,          ) =>      page,      transitionsBuilder: (          BuildContext context,          Animation<double> animation,          Animation<double> secondaryAnimation,          Widget child,          ) =>          SlideTransition(            position: Tween<Offset>(              begin: const Offset(1.0, 0.0), // تغيير القيمة هنا لتكون (1.0, 0.0)              end: Offset.zero,            ).animate(animation),            child: child,          ),      transitionDuration: const Duration(milliseconds: 400)  );}